import React, { useContext, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Header from "./components/Header";
import Footer from "./components/Footer";
import AdminDashboard from "./pages/AdminDashboard";
import MemberDashboard from "./pages/MemberDashboard";
import AccountPage from "./pages/AccountPage";
import EventsPage from "./pages/EventsPage";
import NewsLetters from "./components/NewsLetter";
import LoginForm from "./components/LoginForm";
import RegisterForm from "./components/RegisterForm";
import { AuthContext, AuthProvider } from "./context/AuthContext";
import "./App.css";

const App = () => {
  const { currentUser, login, logout, loading } = useContext(AuthContext);

  useEffect(() => {
    // This effect ensures that the app loads the user state on mount
  }, []);

  // Route guards for admin and member-specific pages
  const ProtectedRoute = ({ children, role }) => {
    if (loading) {
      return <div>Loading...</div>; // Show a loading indicator while loading the user
    }
    if (!currentUser) {
      return <Navigate to="/login" replace />;
    }
    if (role && currentUser.role !== role) {
      return <Navigate to="/" replace />;
    }
    return children;
  };

  return (
    <AuthProvider>
      <Router>
        <div className="app">
          <Header />
          <main>
            <Routes>
              {/* Public Routes */}
              <Route path="/login" element={<LoginForm />} />
              <Route path="/register" element={<RegisterForm />} />

              {/* Member-Specific Routes */}
              <Route
                path="/member/dashboard"
                element={
                  <ProtectedRoute role="member">
                    <MemberDashboard />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/member/account"
                element={
                  <ProtectedRoute role="member">
                    <AccountPage />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/member/events"
                element={
                  <ProtectedRoute role="member">
                    <EventsPage />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/member/newsletters"
                element={
                  <ProtectedRoute role="member">
                    <NewsLetters />
                  </ProtectedRoute>
                }
              />

              {/* Admin-Specific Routes */}
              <Route
                path="/admin/dashboard"
                element={
                  <ProtectedRoute role="admin">
                    <AdminDashboard />
                  </ProtectedRoute>
                }
              />

              {/* Default Route */}
              <Route
                path="/"
                element={
                  currentUser ? (
                    currentUser.role === "admin" ? (
                      <Navigate to="/admin/dashboard" replace />
                    ) : (
                      <Navigate to="/member/dashboard" replace />
                    )
                  ) : (
                    <Navigate to="/login" replace />
                  )
                }
              />
            </Routes>
          </main>
          <Footer />
        </div>
      </Router>
    </AuthProvider>
  );
};

export default App;
